import React,{useState , useRef} from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import Error from "../UI/Error";
import './AddUsers.css';

const AddUsers = (props) =>{

  const nameInputRef = useRef();
  const ageInputRef = useRef();

  const [name , setName] = useState('');
  const [age , setAge] = useState('');
  const [error, setError] = useState();


   const submitHandler = (event) =>{
        event.preventDefault();
        console.log(nameInputRef.current.value)
        // const name = nameInputRef.current.value;
        // const age = ageInputRef.current.value
        if(name.trim().length === 0 || age.trim().length === 0){
          setError({
            title:"Invalid input",
            message:"Please enter a valid name and age"
          })
          return;
        }
        if(+age < 1){
          setError({
            title:"Invalid input",
            message:"Please enter a valid age ( > 0 )"
          })
          return
        }
        props.onAddUser(name,age)
        setAge('')
        setName('')
        // console.log(age,name)
    }

    const usernameHandler = (event) =>{
      setName(event.target.value)
    }

    const ageHandler = (event) =>{
      setAge(event.target.value) 
    }

    const errorHandler = () =>{
      setError(null)
    }

  return ( 
    <>
{  error &&  <Error onHandleError = {errorHandler} title = {error.title} message = {error.message}/>
}   


 <Card >
    <form onSubmit={submitHandler}>
        <label>Username</label>
        <input ref={nameInputRef} id ="username" value={name} type="text" onChange={usernameHandler} />

        <label>Age</label>
        <input ref={ageInputRef} id ="age" type="number" value={age} onChange={ageHandler}/>

        <Button type="submit">Add User</Button>
    </form>
    </Card>
    </>
  )
}

export default AddUsers;
